import React from 'react'
import ReactDOM from 'react-dom'
import css_logo from './images/css_logo.png'
import html_logo from './images/html_logo.png'
import js_logo from './images/js_logo.png'
import logo from './images/react_logo.png'
import foto from './images/asabeneh.jpg'


const Header=() => {
  <header>
    <h1>Front end Technologies</h1>
    <div className="images_logos">
      <img src={html_logo} alt="logo html"></img>
      <img src={css_logo} alt="logo css"></img>
      <img src={js_logo} alt="logo js"></img>
      <img src={logo} alt="logo react"></img>
    </div>
  </header>
}





// Hexadecimal color generator
const hexaColor= () => {
  let arreglo="0123456789abcdef";
  let color=""

  for (let i=0;i<6;i++)
  {
    let elem=Math.floor(Math.random()*arreglo.length)
    color=color+arreglo[elem]
  }
  color="#"+color
  console.log(color)
  return color

}

const HexaColor = () => <div>{hexaColor()}</div>

const rootElement = document.getElementById('root')
// we render the App component using the ReactDOM package
ReactDOM.render(<HexaColor />, rootElement)
