import React from 'react'
import ReactDOM from 'react-dom'
import asabaneh from './images/asabeneh.jpg'



class Header extends React.Component {
    constructor(props) {
        super(props)
    }

    render() {

        const { 
            welcome,
            title,
            subtitle,
            author:{ firstName, lastName},
            date
        }= this.props.data


        return(
            <header>
                <div className="header-wrapper">
                    <h1>{welcome}</h1>
                    <h2>{title}</h2>
                    <h3>{subtitle}</h3>
                    <p>{firstName} {lastName}</p>
                    <small>{date}</small>
                </div>
            </header>
        )
    }
}

const Lista=(arreglo)=>
{
    const lista=arreglo.map((elem)=><li key={elem}>{elem}</li>)
    return lista
}



const Suma=()=>
(
    <div>
        <p>
            3+2=5
        </p>
    </div>
)


class Main extends React.Component {
    constructor(props) {
        super(props)
    }

    render()
    {

        const arreglo=["HTML","CSS","Javascript"]
        return (
            <div class="main-wrapper">
                <h3>Prerequisite to get started <strong>react.js</strong></h3>
                <ul>
                    <Lista lista={arreglo}/>
                </ul>
                <Suma/> 
            </div>
        )
    }
}
