const showDate = time => {
  const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December']; //slice agarra las primeras tres letras de cada mes

  const month = months[time.getMonth()].slice(0, 3);
  const year = time.getFullYear();
  const day = time.getDate();
  return `${day}/${month}/${year}`;
};
